import { useState } from "react";

const Example = () => {
  const [countA, setCountA] = useState(0);
  return (
    <>
      <p>ボタンAを{countA}回押しました!</p>
      <button
        onClick={() => {
          setCountA(countA + 1);
        }}
      >
        ボタンA
      </button>
      <p>ボタンBを{}回押しました!</p>
      <p>ボタンCを{}回押しました!</p>
    </>
  );
};

export default Example;
