import { useState } from "react";

const Example = () => {
  const [count, setCount] = useState(0);

  return (
    <>
      <CountResult title="カウント" count={count} />
      <CountUpdate setCont={setCount} />
    </>
  );
};
const CountResult = () => <h3>{/* propsのtitleとcountの値を表示 */}</h3>;

const CountUpdate = ({ title, count }) => {
  const countUp = () => {
    {
      title;
    }
    {
      count;
    }
  };
  const countDown = () => {
    /* countから1マイナス */
  };
  return (
    <>
      <button onClick={countUp}>+</button>
      <button onClick={countDown}>-</button>
    </>
  );
};

export default Example;
